mapper.select:
SELECT post_id
FROM post_favorite
WHERE user_id = #{userId}

mapper.delete:
DELETE FROM post_favorite
WHERE user_id = #{userId}
  AND post_id = #{postId}

mapper.select:
SELECT COUNT(*)
FROM post_favorite
WHERE user_id = #{userId}

SELECT COUNT(*)
FROM post_favorite
WHERE user_id = #{userId}
  AND post_id = #{postId}
