mapper.select:
SELECT id, follower_id, following_id, created_at
FROM user_follow
WHERE id = #{id}

SELECT id, follower_id, following_id, created_at
FROM user_follow
WHERE follower_id = #{followerId}
ORDER BY id DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM user_follow
WHERE follower_id = #{followerId}

mapper.delete:
DELETE FROM user_follow
WHERE follower_id = #{followerId}
  AND following_id = #{followingId}

mapper.select:
SELECT COUNT(*)
FROM user_follow
WHERE follower_id = #{followerId}
  AND following_id = #{followingId}

SELECT COUNT(*)
FROM user_follow
WHERE following_id = #{followingId}
